import React, { useState } from "react";
import { FaCartArrowDown } from "react-icons/fa";

const Products = () => {
  const [cart, setCart] = useState({});

  const product = [
    { id: 1, name: "Product 1", description: "Description 1", price: 100 },
    { id: 2, name: "Product 2", description: "Description 2", price: 150 },
    { id: 3, name: "Product 3", description: "Description 3", price: 200 },
    { id: 4, name: "Product 4", description: "Description 4", price: 250 },
  ];

  const addToCart = (product) => {
    setCart((prevCart) => ({
      ...prevCart,
      [product.id]: (prevCart[product.id] || 0) + 1,
    }));
  };

  const removeFromCart = (product) => {
    setCart((prevCart) => {
      if (!prevCart[product.id]) return prevCart;
      const updatedCart = { ...prevCart };
      updatedCart[product.id]--;
      if (updatedCart[product.id] === 0) delete updatedCart[product.id];
      return updatedCart;
    });
  };

  const getTotalItems = () => {
    return Object.values(cart).reduce((sum, count) => sum + count, 0);
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      {/* Cart Section */}
      <div style={{ marginBottom: "20px" }}>
        <FaCartArrowDown size={30} />
        <input type="text" value={getTotalItems()} readOnly style={{ width: "40px", textAlign: "center", marginLeft: "10px" }} />
        <button style={{ marginLeft: "10px", padding: "5px 10px", backgroundColor: "green", color: "white", border: "none" }}>
          Checkout
        </button>
      </div>

      {/* Products Section */}
      <div style={{ display: "grid", gridTemplateColumns: "repeat(2, 1fr)", gap: "20px", justifyContent: "center" }}>
        {products.map((product) => (
          <div key={product.id} style={{ border: "1px solid gray", padding: "10px", borderRadius: "10px", width: "250px" }}>
            <img src="" alt="No preview" style={{ width: "100%", height: "150px", backgroundColor: "#f3f3f3" }} />
            <h1 style={{ fontSize: "18px" }}>{product.name}</h1>
            <h2 style={{ fontSize: "14px", color: "gray" }}>{product.description}</h2>
            <p style={{ fontWeight: "bold" }}>Price: â‚¹{product.price}</p>
            <button onClick={() => addToCart(product)} style={{ padding: "5px 10px", backgroundColor: "#007bff", color: "white", border: "none", marginRight: "5px" }}>
              Add to Cart
            </button>
            <button
              onClick={() => removeFromCart(product)}
              disabled={!cart[product.id]}
              style={{ padding: "5px 10px", backgroundColor: "#dc3545", color: "white", border: "none" }}
            >
              Remove
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
